import React from 'react';
import {
  Form,
  Input,
  Button,
  TreeSelect,
  Upload, Image, Space,
} from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import PropTypes from 'prop-types';

const PremisesForm = ({ defaultValues, onFormSubmit, isLoading }) => {
  const [form] = Form.useForm();
  const onFinish = (values) => {
    onFormSubmit(values);
  };
  const { TextArea } = Input;
  return (
    <>
      <Form
        form={form}
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
        onFinish={onFinish}
        initialValues={{ ...defaultValues }}
      >
        <Form.Item label="Name" name="name" rules={[{ required: true, message: 'Name is required' }]}>
          <Input />
        </Form.Item>
        <Form.Item label="Description" name="description" rules={[{ required: true, message: 'Description is required' }]}>
          <TextArea rows={4} />
        </Form.Item>
        <Form.Item
          label="Upload Thumbnail"
          name="image"
          rules={[{ required: !defaultValues?.image, message: 'Image is required' }]}
        >
          <Upload
            accept=".png"
            beforeUpload={() => false}
            multiple={false}
          >
            <Space>
              <Button icon={<UploadOutlined />}>
                Click to Upload
              </Button>
              <Image
                width={100}
                height={100}
                src={defaultValues?.image}
                fallback="data:image/png;base64,"
              />
            </Space>
          </Upload>
        </Form.Item>
        <Form.Item label="City" name="city" rules={[{ required: true, message: 'City is required' }]}>
          <TreeSelect
            treeData={[
              { title: 'Gdynia', value: 'Gdynia' },
            ]}
          />
        </Form.Item>
        <Form.Item label="Postcode" name="postcode" rules={[{ required: true, message: 'Postcode is required' }]}>
          <Input />
        </Form.Item>
        <Form.Item label="Country" name="country" rules={[{ required: true, message: 'Country is required' }]}>
          <TreeSelect
            treeData={[
              { title: 'Poland', value: 'Poland' },
            ]}
          />
        </Form.Item>
        <Form.Item name="address" label="Address" rules={[{ required: true, message: 'Address is required' }]}>
          <Input />
        </Form.Item>
        <Form.Item wrapperCol={{ span: 8, offset: 4 }}>
          <Button type="primary" htmlType="submit" loading={isLoading}>
            Submit
          </Button>
        </Form.Item>

      </Form>
    </>
  );
};

PremisesForm.propTypes = {
  defaultValues: PropTypes.shape({
    name: PropTypes.string,
    city: PropTypes.string,
    country: PropTypes.string,
    description: PropTypes.string,
    image: PropTypes.string,
    postcode: PropTypes.string,
  }),
  onFormSubmit: PropTypes.func.isRequired,
  isLoading: PropTypes.bool,
};

PremisesForm.defaultProps = {
  defaultValues: {
    name: '',
    city: '',
    country: '',
    description: '',
    image: '',
    postcode: '',
  },
  isLoading: false,
};
export default PremisesForm;
