import React from 'react';
import {
  Form,
  Input,
  Button,
  TreeSelect,
  Upload, Image, Space, Select,
} from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import PropTypes from 'prop-types';

const ProductForm = ({
  defaultValues, onFormSubmit, isLoading, premises, fetchMenusByPremises,
}) => {
  const [form] = Form.useForm();
  const onFinish = (values) => {
    onFormSubmit(values);
  };

  const { data } = fetchMenusByPremises(form.premises);
  const { TextArea } = Input;
  return (
    <>
      <Form
        form={form}
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
        onFinish={onFinish}
        initialValues={{ ...defaultValues }}
      >
        <Form.Item label="Name" name="name" rules={[{ required: true, message: 'Name is required' }]}>
          <Input />
        </Form.Item>
        <Form.Item label="Description" name="description" rules={[{ required: true, message: 'Description is required' }]}>
          <TextArea rows={4} />
        </Form.Item>
        <Form.Item label="Price" name="price" rules={[{ required: true, message: 'Price is required' }]}>
          <Input />
        </Form.Item>
        <Form.Item
          label="Upload Thumbnail"
          name="image"
          rules={[{ required: !defaultValues?.image, message: 'Image is required' }]}
        >
          <Upload
            accept=".png"
            beforeUpload={() => false}
            multiple={false}
          >
            <Space>
              <Button icon={<UploadOutlined />}>
                Click to Upload
              </Button>
              <Image
                width={100}
                height={100}
                src={defaultValues?.image}
                fallback="data:image/png;base64,"
              />
            </Space>
          </Upload>
        </Form.Item>
        <Form.Item label="Premises" name="premises" rules={[{ required: true, message: 'Premises is required' }]}>
          <Select
            style={{ width: 200 }}
            options={premises?.map((premise) => ({ value: premise.id, label: premise.name }))}
            loading={isLoading}
          />
        </Form.Item>
        <Form.Item label="Menu" name="Menu" rules={[{ required: true, message: 'Menu is required' }]}>
          <Select
            style={{ width: 200 }}
            options={data?.map((menu) => ({ value: menu.id, label: menu.name }))}
            loading={isLoading}
          />
        </Form.Item>
        <Form.Item label="Estimated Creation Time" name="estimated_creation_time" rules={[{ required: true, message: 'Postcode is required' }]}>
          <Input />
        </Form.Item>
        <Form.Item name="ingredients" label="Ingredients" rules={[{ required: true, message: 'Address is required' }]}>
          <Input />
        </Form.Item>
        <Form.Item wrapperCol={{ span: 8, offset: 4 }}>
          <Button type="primary" htmlType="submit" loading={isLoading}>
            Submit
          </Button>
        </Form.Item>

      </Form>
    </>
  );
};

ProductForm.propTypes = {
  defaultValues: PropTypes.shape({
    name: PropTypes.string,
    description: PropTypes.string,
    price: PropTypes.number,
    ingredients: PropTypes.string,
    estimated_creation_time: PropTypes.number,
    premises: PropTypes.number,
    menu: PropTypes.number,
    image: PropTypes.string,
  }),
  premises: PropTypes.arrayOf(PropTypes.object).isRequired,
  onFormSubmit: PropTypes.func.isRequired,
  isLoading: PropTypes.bool,
};

ProductForm.defaultProps = {
  defaultValues: {
    name: '',
    description: '',
    price: '',
    ingredients: '',
    estimated_creation_time: '',
    premises: null,
    menu: null,
    image: null,
  },
  isLoading: false,
};
export default ProductForm;
